import React from 'react';
import './App.css';

class BreakLength extends React.Component
{
	render()
	{
		return(
			<div className="BreakLength">
				<button id="add" data='1' onClick={this.props.handleClick}>One minute more</button>
				<div id="displayBreakLength">{(this.props.breakLength).toString()}</div>
				<button id="reduce" data='-1' onClick={this.props.handleClick}>One minute less</button>
			</div>
		);
	}
}
class App extends React.Component 
{
	constructor(props)
	{
		super(props);
		this.state = {
			'breakLength': 5,
			'sessionLength': 25
		};
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick(e)
	{
		console.log(parseInt(e.currentTarget.data));
		let newValue = this.state.breakLength + parseInt(e.currentTarget.data);
		this.setState({breakLength: newValue});
	}
	render()
	{
		return (
			<div className="App">
				<BreakLength breakLength={this.state.breakLength} handleClick={this.handleClick}/>
			</div>
		);
	}
}

export default App;
